mapper.select:
SELECT MENU_PK, MENU_NAME, MENU_TYPE, MENU_LINK, MENU_SORT, MENU_FATHER_PK FROM SC_MENU
AND MENU_FATHER_PK IS NULL AND FK_APP = #{FK_APP} AND MENU_PLAT = #{MENU_PLAT}
ORDER BY CONVERT(MENU_SORT, SIGNED)

SELECT MENU_PK, MENU_NAME, MENU_TYPE,
MENU_LINK, MENU_SORT,
MENU_FATHER_PK
FROM SC_MENU

	AND MENU_FATHER_PK = #{MENU_FATHER_PK}
	AND FK_APP = #{FK_APP}
	AND MENU_PLAT =
	#{MENU_PLAT}

ORDER BY CONVERT(MENU_SORT, SIGNED)

SELECT MENU_PK, MENU_NAME, MENU_TYPE, MENU_LINK, MENU_SORT, MENU_FATHER_PK FROM SC_MENU
AND FK_APP = #{FK_APP} AND MENU_PLAT = #{MENU_PLAT}
ORDER BY CONVERT(MENU_SORT, SIGNED), MENU_FATHER_PK

SELECT count(MENU_PK) as nums
FROM SC_MENU


		AND MENU_FATHER_PK = #{MENU_FATHER_PK}

	AND FK_APP = #{FK_APP}
	AND MENU_PLAT = #{MENU_PLAT}

SELECT FK_MENU_WX
FROM SC_APP_MENU

	AND MENU_PLAT = #{MENU_PLAT}
	AND FK_APP = #{FK_APP}

delete
FROM SC_APP_MENU

	AND MENU_PLAT = #{MENU_PLAT}
	AND FK_APP = #{FK_APP}

