mapper.select:
select o.ORDER_PK, (select t.TABLES_NAME from sc_tables t where t.TABLES_PK = o.ORDER_POSITION ) AS ORDER_POSITION,o.ORDER_PAY_WAY,o.ORDER_DIVISION, o.ORDER_CODE,o.ORDER_RS,o.ORDER_PAY_STATE,o.ORDER_STATE, o.ORDER_YFMONEY,o.ORDER_YHMONEY,o.ORDER_MLMONEY, o.ORDER_JFDXMONEY,o.ORDER_SHOPMONEY, o.ARRIVE_TIME,o.CREATE_TIME,o.CREATE_BY, SUM(od.ORDER_DETAILS_FS) AS TOTAL_FS, SUM(od.ORDER_DETAILS_GMONEY/100*od.ORDER_DETAILS_FS) AS TOTAL_MONEY from SC_ORDER o left join SC_ORDER_DETAILS od on o.ORDER_PK = od.FK_ORDER WHERE (o.ORDER_DIVISION = '0' OR o.ORDER_DIVISION = '2')
AND ORDER_PAY_STATE = #{ORDER_PAY_STATE}
AND FK_SHOP = #{FK_SHOP} GROUP BY od.FK_ORDER ORDER BY str_to_date(CREATE_TIME,'%Y-%m-%d %H:%i:%s') DESC

mapper.delete:
UPDATE  SC_ORDER_DETAILS od, sc_order o
	SET od.ORDER_DETAILS_GOODS_STATE = '3', o.ORDER_YFMONEY = o.ORDER_YFMONEY - od.ORDER_DETAILS_GMONEY
	WHERE o.ORDER_PK = od.FK_ORDER AND od.ORDER_DETAILS_PK = #{ORDER_DETAILS_PK} AND od.ORDER_DETAILS_GOODS_STATE != '3';

mapper.insert:
INSERT INTO SC_ORDER
ORDER_PK, ORDER_POSITION, ORDER_CODE, ORDER_RS, ORDER_PAY_STATE, ORDER_STATE, ARRIVE_TIME, FK_USER, FK_SHOP, CREATE_TIME, CREATE_BY, ORDER_YFMONEY, ORDER_DIVISION
#{UUID}, #{ORDER_POSITION}, #{ORDER_CODE}, #{ORDER_RS}, '0', '3', NOW(), #{FK_USER}, #{FK_SHOP}, NOW(), #{CREATE_BY}, #{ORDER_YFMONEY}, #{ORDER_DIVISION}

INSERT INTO SC_ORDER_DETAILS
ORDER_DETAILS_PK, ORDER_DETAILS_GNAME, ORDER_DETAILS_FS, ORDER_DETAILS_GMONEY, FK_ORDER, ORDER_DETAILS_FORMAT, ORDER_DETAILS_TASTE, ORDER_DETAILS_MAKING, ORDER_DETAILS_DW, FK_GOODS
#{UUID}, #{ORDER_DETAILS_GNAME}, #{ORDER_DETAILS_FS}, #{ORDER_DETAILS_GMONEY}, #{FK_ORDER}, #{ORDER_DETAILS_FORMAT}, #{ORDER_DETAILS_TASTE}, #{ORDER_DETAILS_MAKING}, #{ORDER_DETAILS_DW}, #{FK_GOODS}

mapper.select:
SELECT COUNT(ORDER_PK) as ORDER_COUNT FROM SC_ORDER WHERE
ORDER_PAY_STATE = '0' AND ORDER_STATE = '2' AND FK_SHOP = #{FK_SHOP}
AND CREATE_BY = #{FK_USER}

mapper.update:
UPDATE sc_tables t, sc_order o
SET t.TABLES_ISUSE = 0, o.ORDER_PAY_WAY = #{ORDER_PAY_WAY}, o.ORDER_PAY_STATE = #{ORDER_PAY_STATE}, ORDER_MLMONEY = #{ORDER_MLMONEY}, ORDER_JFDXMONEY = #{ORDER_JFDXMONEY},
	ORDER_YHMONEY = #{ORDER_YHMONEY}, ORDER_SHOPMONEY = #{ORDER_SHOPMONEY}
WHERE o.ORDER_PK = #{ORDER_PK} AND t.TABLES_PK = o.ORDER_POSITION;

UPDATE sc_order 
SET ORDER_STATE = #{ORDER_STATE},
UPDATE_TIME = NOW(),
UPDATE_BY = #{OPEN_ID}
WHERE ORDER_PK = #{ORDER_PK}

UPDATE sc_tables t, sc_order o
SET t.TABLES_ISUSE = 0, o.ORDER_STATE = #{ORDER_STATE},
o.UPDATE_TIME = NOW(),
o.UPDATE_BY = #{OPEN_ID}
WHERE o.ORDER_PK = #{ORDER_PK} AND t.TABLES_PK = o.ORDER_POSITION;

UPDATE sc_order o LEFT JOIN sc_tables t on t.TABLES_PK = o.ORDER_POSITION SET t.TABLES_ISUSE = '0',o.ORDER_PAY_STATE = '1',o.PAY_TIME = NOW(), o.UPDATE_TIME = NOW()

	,o.ORDER_PAY_WAY = #{ORDER_PAY_WAY}
  WHERE o.ORDER_PK = #{ORDER_PK};

mapper.select:
SELECT ur.VCARD_NUMBER, ur.USER_VCARD_JF, ur.USER_VCARD_CZ 
FROM sc_order o LEFT JOIN sc_user_vcard ur ON o.IS_USE_VCARD = '1' AND o.USER_VCARD_FK = ur.USER_VCARD_PK 
WHERE o.ORDER_PK = #{ORDER_PK};
