mapper.select:
select o.ORDER_PK,o.ORDER_POSITION,o.ORDER_PAY_WAY, o.ORDER_CODE,o.ORDER_RS,o.ORDER_PAY_STATE,o.ORDER_STATE, o.ARRIVE_TIME,o.CREATE_TIME,o.CREATE_BY, SUM(od.ORDER_DETAILS_FS) AS TOTAL_FS, SUM(od.ORDER_DETAILS_GMONEY/100*od.ORDER_DETAILS_FS) AS TOTAL_MONEY from SC_ORDER o left join SC_ORDER_DETAILS od on o.ORDER_PK = od.FK_ORDER WHERE str_to_date(CREATE_TIME,'%Y-%m-%d %H:%i') BETWEEN str_to_date(#{CREATE_TIME},'%Y-%m-%d %H:%i') AND str_to_date(#{END_TIME},'%Y-%m-%d %H:%i')
AND ORDER_PAY_STATE = #{ORDER_PAY_STATE}
AND FK_SHOP = #{FK_SHOP} GROUP BY od.FK_ORDER ORDER BY str_to_date(CREATE_TIME,'%Y-%m-%d %H:%i:%s') DESC
