mapper.update:
update sc_preferntial set 
	delete_flag = '1',
	update_time = date_format(NOW(), '%Y-%c-%d %h:%i:%s' ),
	update_by = #{userId}
where preferntial_pk = #{favPk}

update sc_preferntial_shop set 
	delete_flag = '1'
where fk_preferntial = #{favPk}

mapper.select:
select preferential_rule_pk, rule_name, rule_order, is_use, rule_model, remark, good_scope, create_by, create_time, delete_flag, fk_shop from sc_preferntial_rule where delete_flag = '0'
AND preferential_rule_pk = #{rulePk}
AND fk_shop = #{shopId}
and create_by = #{userId}
order by rule_order asc

mapper.update:
UPDATE 
	sc_shop_info SET SHOP_DMZKXS = #{SHOP_DMZKXS}, SHOP_DMJFXS = #{SHOP_DMJFXS}, SHOP_SRZKXS = #{SHOP_SRZKXS}, SHOP_SRJFXS = #{SHOP_SRJFXS}
WHERE
	 SHOP_ID = #{SHOP_ID};
