mapper.select:
SELECT COUNT(FK_GOODS) as GOODS_COUNT, GOODS_NUMBER FROM
SC_SHOPPING_CART

	AND FK_SHOP = #{FK_SHOP}
	AND FK_USER = #{FK_USER}
	AND FK_GOODS =
	#{GOODS_PK}
	AND CART_STATE = 'zancun'

		AND GOODS_MAKING = #{GOODS_RECIPE}


		AND GOODS_FORMAT = #{GOODS_SPECIFICATION}


		AND GOODS_TASTE = #{GOODS_TASTE}

mapper.update:
UPDATE SC_SHOPPING_CART SET GOODS_NUMBER = #{GOODS_NUMBER}

	AND FK_SHOP = #{FK_SHOP}
	AND FK_USER = #{FK_USER}
	AND FK_GOODS =
	#{GOODS_PK}
	AND CART_STATE = 'zancun'

		AND GOODS_MAKING = #{GOODS_RECIPE}


		AND GOODS_FORMAT = #{GOODS_SPECIFICATION}


		AND GOODS_TASTE = #{GOODS_TASTE}

UPDATE SC_SHOPPING_CART SET GOODS_NUMBER = #{GOODS_NUM}

	AND CART_PK = #{CART_PK}
	AND CART_STATE = 'zancun'

mapper.delete:
delete FROM SC_SHOPPING_CART

	AND FK_SHOP = #{FK_SHOP}
	AND FK_USER = #{FK_USER}
	AND FK_GOODS =
	#{GOODS_PK}
	AND CART_STATE = 'zancun'

		AND GOODS_MAKING = #{GOODS_RECIPE}


		AND GOODS_FORMAT = #{GOODS_SPECIFICATION}


		AND GOODS_TASTE = #{GOODS_TASTE}

delete FROM SC_SHOPPING_CART

	AND FK_SHOP = #{shopid}
	AND FK_USER = #{openid}
	AND CART_STATE =
	'zancun'
