mapper.update:
UPDATE sc_tables_area SET TABLES_AREA_PXXH = CAST(TABLES_AREA_PXXH AS SIGNED integer) + 1 WHERE SHOP_FK = #{SHOP_ID} AND CAST(TABLES_AREA_PXXH AS SIGNED integer) >= CAST(#{SMALL_TABLES_AREA_PXXH} AS SIGNED integer)
AND CAST(TABLES_AREA_PXXH AS SIGNED integer) < CAST(#{BIG_TABLES_AREA_PXXH} AS SIGNED integer)
;

UPDATE sc_tables_area SET TABLES_AREA_PXXH = CAST(TABLES_AREA_PXXH AS SIGNED integer) - 1 WHERE SHOP_FK = #{SHOP_ID} AND CAST(TABLES_AREA_PXXH AS SIGNED integer) > CAST(#{SMALL_TABLES_AREA_PXXH} AS SIGNED integer)
AND CAST(TABLES_AREA_PXXH AS SIGNED integer) <= CAST(#{BIG_TABLES_AREA_PXXH} AS SIGNED integer);
;
